import { Injectable } from '@angular/core';
import { HttpRequest, HttpHandler, HttpEvent, HttpInterceptor } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable()
export class AuthInterceptor implements HttpInterceptor {

  constructor() {}

  intercept(request: HttpRequest<unknown>, next: HttpHandler): Observable<HttpEvent<unknown>> {
    // Get the auth token from localStorage
    const authToken = localStorage.getItem('sgos_token');
    
    console.log('üîê AuthInterceptor - URL:', request.url);
    console.log('üîê AuthInterceptor - Token:', authToken ? 'Presente' : 'Ausente');

    // If we have a token, clone the request and add the authorization header
    if (authToken) {
      const authRequest = request.clone({
        headers: request.headers.set('Authorization', `Bearer ${authToken}`)
      });
      console.log('üîê AuthInterceptor - Headers adicionados:', authRequest.headers.get('Authorization'));
      return next.handle(authRequest);
    }

    // If no token, proceed with the original request
    console.log('üîê AuthInterceptor - Sem token, prosseguindo sem autentica√ß√£o');
    return next.handle(request);
  }
}
