import { Component, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { Platform, MenuController } from '@ionic/angular';
import { Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';

import { AuthService } from './services/auth.service';
import { Usuario } from './models';

interface MenuItem {
  title: string;
  url: string;
  icon: string;
  requiredRole?: string;
  badge?: number;
}

@Component({
  selector: 'app-root',
  templateUrl: 'app.component.html',
  styleUrls: ['app.component.scss'],
})
export class AppComponent implements OnInit, OnDestroy {
  private destroy$ = new Subject<void>();
  
  currentUser: Usuario | null = null;
  isAuthenticated = false;
  isMobile = false;
  isMenuOpen = false;

  menuItems: MenuItem[] = [
    {
      title: 'Dashboard',
      url: '/dashboard',
      icon: 'home-outline'
    },
    {
      title: 'Ordens de Serviço',
      url: '/ordens-servico',
      icon: 'construct-outline'
    },
    {
      title: 'Veículos',
      url: '/veiculos',
      icon: 'car-outline'
    },
    {
      title: 'Usuários',
      url: '/usuarios',
      icon: 'people-outline',
      requiredRole: 'SUPERVISOR'
    },
    {
      title: 'Relatórios',
      url: '/relatorios',
      icon: 'bar-chart-outline',
      requiredRole: 'SUPERVISOR'
    },
    {
      title: 'Configurações',
      url: '/configuracoes',
      icon: 'settings-outline',
      requiredRole: 'ADMIN'
    }
  ];

  bottomMenuItems: MenuItem[] = [
    {
      title: 'Perfil',
      url: '/perfil',
      icon: 'person-outline'
    }
  ];

  constructor(
    private platform: Platform,
    private router: Router,
    private menuController: MenuController,
    private authService: AuthService
  ) {
    this.initializeApp();
  }

  ngOnInit() {
    // Subscribe to authentication state
    this.authService.isAuthenticated$
      .pipe(takeUntil(this.destroy$))
      .subscribe(isAuth => {
        this.isAuthenticated = isAuth;
        if (!isAuth) {
          this.menuController.close();
        }
      });

    // Subscribe to current user
    this.authService.currentUser$
      .pipe(takeUntil(this.destroy$))
      .subscribe(user => {
        this.currentUser = user;
      });

    // Check platform
    this.isMobile = this.platform.is('mobile');
    
    // Handle platform resize
    this.platform.resize
      .pipe(takeUntil(this.destroy$))
      .subscribe(() => {
        this.isMobile = this.platform.is('mobile');
      });
  }

  ngOnDestroy() {
    this.destroy$.next();
    this.destroy$.complete();
  }

  private async initializeApp() {
    await this.platform.ready();
    
    // Check token expiration periodically
    setInterval(() => {
      this.authService.checkTokenExpiration();
    }, 60000); // Check every minute
  }

  /**
   * Check if user has permission to see menu item
   */
  hasPermission(item: MenuItem): boolean {
    if (!item.requiredRole) return true;
    return this.authService.hasPermission(item.requiredRole);
  }

  /**
   * Get filtered menu items based on user permissions
   */
  getFilteredMenuItems(): MenuItem[] {
    return this.menuItems.filter(item => this.hasPermission(item));
  }

  /**
   * Navigate to page and close menu on mobile
   */
  async navigateTo(url: string) {
    await this.router.navigate([url]);
    
    if (this.isMobile) {
      await this.menuController.close();
    }
  }

  /**
   * Logout user
   */
  async logout() {
    await this.authService.logout();
    await this.menuController.close();
  }

  /**
   * Toggle menu
   */
  async toggleMenu() {
    await this.menuController.toggle();
  }

  /**
   * Close menu
   */
  async closeMenu() {
    await this.menuController.close();
  }

  /**
   * Get user initials for avatar
   */
  getUserInitials(): string {
    if (!this.currentUser?.nome_completo) return 'U';
    
    const names = this.currentUser.nome_completo.split(' ');
    if (names.length >= 2) {
      return (names[0][0] + names[1][0]).toUpperCase();
    }
    return names[0][0].toUpperCase();
  }

  /**
   * Get user role display name
   */
  getUserRoleDisplayName(): string {
    if (!this.currentUser) return '';
    
    const roleNames = {
      'ADMIN': 'Administrador',
      'SUPERVISOR': 'Supervisor',
      'MECANICO': 'Mecânico',
      'USUARIO': 'Usuário'
    };
    
    return roleNames[this.currentUser.perfil as keyof typeof roleNames] || this.currentUser.perfil;
  }

  /**
   * Handle menu state change
   */
  onMenuStateChange(isOpen: boolean) {
    this.isMenuOpen = isOpen;
  }

  /**
   * Get current route for menu highlighting
   */
  isCurrentRoute(url: string): boolean {
    return this.router.url === url;
  }
}
