    </ion-header>
    
    <ion-content>
      <!-- User Info -->
      <div class="user-info" *ngIf="currentUser">
        <div class="user-avatar">
          <div class="avatar-circle">
            {{ getUserInitials() }}
          </div>
        </div>
        <div class="user-details">
          <h3>{{ currentUser.nome_completo }}</h3>
          <p>{{ getUserRoleDisplayName() }}</p>
          <small>{{ currentUser.email }}</small>
        </div>
      </div>

      <ion-list>
        <!-- Main Menu Items -->
        <ion-item 
          *ngFor="let item of getFilteredMenuItems()" 
          button 
          (click)="navigateTo(item.url)"
          [class.active-menu-item]="isCurrentRoute(item.url)">
          <ion-icon [name]="item.icon" slot="start"></ion-icon>
          <ion-label>{{ item.title }}</ion-label>
          <ion-badge *ngIf="item.badge" slot="end" color="danger">
            {{ item.badge }}
          </ion-badge>
        </ion-item>

        <ion-item-divider>
          <ion-label>Conta</ion-label>
        </ion-item-divider>

        <!-- Bottom Menu Items -->
        <ion-item 
          *ngFor="let item of bottomMenuItems" 
          button 
          (click)="navigateTo(item.url)"
          [class.active-menu-item]="isCurrentRoute(item.url)">
          <ion-icon [name]="item.icon" slot="start"></ion-icon>
          <ion-label>{{ item.title }}</ion-label>
        </ion-item>

        <!-- Logout -->
        <ion-item button (click)="logout()">
          <ion-icon name="log-out-outline" slot="start"></ion-icon>
          <ion-label>Sair</ion-label>
        </ion-item>
      </ion-list>
    </ion-content>
  </ion-menu>

  <!-- Main Router Outlet -->
  <ion-router-outlet id="main-content"></ion-router-outlet>
</ion-app>
