import { Component, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { MenuController, LoadingController, ToastController, AlertController, ModalController } from '@ionic/angular';
import { HttpClient } from '@angular/common/http';
import { Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';

interface OrdemServico {
  id: number;
  data: string;
  veiculo_id: number;
  hodometro: string;
  problema_apresentado: string;
  sistema_afetado: string;
  causa_da_avaria: string;
  manutencao: 'PREVENTIVA' | 'CORRETIVA';
  situacao_os: 'ABERTA' | 'FECHADA' | 'RETIRADA';
  usuario_id: number;
  perfil: string;
  created_at: string;
  veiculo?: {
    id: number;
    marca: string;
    modelo: string;
    placa: string;
    patrimonio: string;
  };
  usuario?: {
    id: number;
    nome: string;
    username: string;
    perfil: string;
  };
  encerrar_os?: {
    id: number;
    nome_mecanico: string;
    data_da_manutencao: string;
    situacao: string;
    tempo_total: string;
    usuario_id: number;
    abrir_os_id: number;
    modelo_veiculo: string;
    created_at: string;
  };
  encerramentos?: {
    id: number;
    nome_mecanico: string;
    data_da_manutencao: string;
    situacao: string;
    tempo_total: string;
    usuario_id: number;
    abrir_os_id: number;
    modelo_veiculo: string;
    created_at: string;
  }[];
}

interface Veiculo {
  id: number;
  marca: string;
  modelo: string;
  placa: string;
  patrimonio: string;
  status: string;
}

@Component({
  selector: 'app-ordens-servico',
  templateUrl: './ordens-servico-simple.page.html',
  styleUrls: ['./ordens-servico.page.scss'],
})
export class OrdensServicoPage implements OnInit, OnDestroy {
  private destroy$ = new Subject<void>();
  
  ordensServico: OrdemServico[] = [];
  filteredOrdens: OrdemServico[] = [];
  veiculos: Veiculo[] = [];
  currentUser: any = null;
  loading = false;
  searchTerm = '';
  selectedStatus = '';
  
  // Create OS form data
  showCreateForm = false;
  createFormData = {
    data: this.getCurrentDate(),
    veiculo_id: '',
    hodometro: '',
    problema_apresentado: '',
    sistema_afetado: '',
    causa_da_avaria: '',
    manutencao: 'PREVENTIVA' as 'PREVENTIVA' | 'CORRETIVA'
  };
  
  statusOptions = [
    { value: '', label: 'Todas' },
    { value: 'ABERTA', label: 'Abertas' },
    { value: 'FECHADA', label: 'Fechadas' },
    { value: 'RETIRADA', label: 'Retiradas' }
  ];

  sistemaOptions = [
    'Motor',
    'Transmissão',
    'Freios',
    'Suspensão',
    'Elétrico',
    'Ar Condicionado',
    'Carroceria',
    'Pneus',
    'Combustível',
    'Outros'
  ];

  constructor(
    private router: Router,
    private menuController: MenuController,
    private loadingController: LoadingController,
    private toastController: ToastController,
    private alertController: AlertController,
    private modalController: ModalController,
    private http: HttpClient
  ) {}

  ngOnInit() {
    // Get user from localStorage
    const userStr = localStorage.getItem('sgos_user');
    if (userStr) {
      this.currentUser = JSON.parse(userStr);
    }
    
    this.loadOrdensServico();
    this.loadVeiculos();
  }

  ngOnDestroy() {
    this.destroy$.next();
    this.destroy$.complete();
  }

  /**
   * Load service orders from API
   */
  async loadOrdensServico() {
    this.loading = true;
    
    try {
      const token = localStorage.getItem('sgos_token');
      const options: any = {
        headers: {
          'Content-Type': 'application/json'
        }
      };
      
      if (token) {
        options.headers['Authorization'] = `Bearer ${token}`;
      }
      
      const response: any = await this.http.get(
        'http://localhost:8000/api/v1/ordens-servico/',
        options
      ).toPromise();
      
      console.log('OS Response:', response);
      
      if (response && (response.status === 'success' || response.success) && response.data) {
        this.ordensServico = response.data.items || [];
        console.log('Loaded OS:', this.ordensServico.length);
        
        // Carregar informações do mecânico para OS fechadas
        await this.loadMechanicInfoForClosedOS();
        
        this.applyFilters();
      } else {
        console.log('No data in response:', response);
        this.ordensServico = [];
        this.applyFilters();
      }
    } catch (error: any) {
      console.error('Error loading service orders:', error);
      await this.showErrorToast('Erro ao carregar ordens de serviço: ' + (error.message || 'Erro desconhecido'));
    } finally {
      this.loading = false;
    }
  }

  /**
   * Load vehicles for selection
   */
  async loadVeiculos() {
    try {
      const token = localStorage.getItem('sgos_token');
      const options: any = {
        headers: {
          'Content-Type': 'application/json'
        }
      };
      
      if (token) {
        options.headers['Authorization'] = `Bearer ${token}`;
      }
      
      const response: any = await this.http.get(
        'http://localhost:8000/api/v1/veiculos/',
        options
      ).toPromise();
      
      console.log('Vehicles Response:', response);
      
      if (response && (response.status === 'success' || response.success) && response.data) {
        this.veiculos = response.data.items || [];
        console.log('Loaded vehicles:', this.veiculos.length);
      } else {
        console.log('No vehicles data:', response);
        this.veiculos = [];
      }
    } catch (error: any) {
      console.error('Error loading vehicles:', error);
    }
  }

  /**
   * Apply search and status filters
   */
  applyFilters() {
    this.filteredOrdens = this.ordensServico.filter(ordem => {
      const matchesSearch = !this.searchTerm || 
        ordem.problema_apresentado.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
        ordem.sistema_afetado.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
        ordem.veiculo?.marca.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
        ordem.veiculo?.modelo.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
        ordem.veiculo?.placa.toLowerCase().includes(this.searchTerm.toLowerCase());
      
      const matchesStatus = !this.selectedStatus || ordem.situacao_os === this.selectedStatus;
      
      return matchesSearch && matchesStatus;
    });
  }

  /**
   * Handle search input
   */
  onSearchChange(event: any) {
    this.searchTerm = event.target.value || '';
    this.applyFilters();
  }

  /**
   * Handle status filter change
   */
  onStatusChange(event: any) {
    this.selectedStatus = event.target.value || '';
    this.applyFilters();
  }

  /**
   * Handle pull-to-refresh
   */
  async onRefresh(event: any) {
    await this.loadOrdensServico();
    event.target.complete();
  }

  /**
   * Show create OS form
   */
  showCreateOS() {
    if (this.veiculos.length === 0) {
      this.showErrorToast('Nenhum veículo disponível. Cadastre veículos primeiro.');
      return;
    }
    
    this.showCreateForm = true;
    this.resetCreateForm();
  }

  /**
   * Hide create OS form
   */
  hideCreateOS() {
    this.showCreateForm = false;
    this.resetCreateForm();
  }

  /**
   * Reset create form
   */
  resetCreateForm() {
    this.createFormData = {
      data: this.getCurrentDate(),
      veiculo_id: '',
      hodometro: '',
      problema_apresentado: '',
      sistema_afetado: '',
      causa_da_avaria: '',
      manutencao: 'PREVENTIVA'
    };
  }

  /**
   * Create new service order
   */
  async createOrdemServico() {
    // Validate form
    if (!this.isCreateFormValid()) {
      await this.showErrorToast('Por favor, preencha todos os campos obrigatórios');
      return;
    }

    // Validate hodometro
    const hodometroNum = parseInt(this.createFormData.hodometro);
    if (isNaN(hodometroNum) || hodometroNum <= 0) {
      await this.showErrorToast('Hodômetro deve ser um número válido maior que zero');
      return;
    }

    const loading = await this.loadingController.create({
      message: 'Criando ordem de serviço...'
    });
    await loading.present();

    try {
      const token = localStorage.getItem('sgos_token');
      const options: any = {
        headers: {
          'Content-Type': 'application/json'
        }
      };
      
      if (token) {
        options.headers['Authorization'] = `Bearer ${token}`;
      }
      
      const formData = {
        ...this.createFormData,
        veiculo_id: parseInt(this.createFormData.veiculo_id),
        hodometro: this.createFormData.hodometro,
        perfil: this.currentUser?.perfil || 'MECANICO',
        situacao_os: 'ABERTA'
      };
      
      const response: any = await this.http.post(
        'http://localhost:8000/api/v1/ordens-servico/',
        formData,
        options
      ).toPromise();
      
      console.log('Create OS Response:', response);
      
      if (response && (response.status === 'success' || response.success)) {
        await this.showSuccessToast('Ordem de serviço criada com sucesso!');
        this.hideCreateOS();
        await this.loadOrdensServico();
      } else {
        throw new Error(response?.message || 'Erro ao criar OS');
      }
    } catch (error: any) {
      console.error('Error creating service order:', error);
      await this.showErrorToast('Erro ao criar ordem de serviço: ' + (error.message || 'Erro desconhecido'));
    } finally {
      await loading.dismiss();
    }
  }

  /**
   * Validate create form
   */
  isCreateFormValid(): boolean {
    const hodometroNum = parseInt(this.createFormData.hodometro);
    return !!(
      this.createFormData.data &&
      this.createFormData.veiculo_id &&
      this.createFormData.hodometro &&
      !isNaN(hodometroNum) &&
      hodometroNum > 0 &&
      this.createFormData.problema_apresentado &&
      this.createFormData.sistema_afetado &&
      this.createFormData.causa_da_avaria
    );
  }

  /**
   * Get current date in YYYY-MM-DD format
   */
  getCurrentDate(): string {
    const today = new Date();
    return today.toISOString().split('T')[0];
  }

  /**
   * Get status color
   */
  getStatusColor(status: string): string {
    switch (status) {
      case 'ABERTA':
        return 'warning';
      case 'FECHADA':
        return 'success';
      case 'RETIRADA':
        return 'primary';
      default:
        return 'medium';
    }
  }

  /**
   * Get status display name
   */
  getStatusDisplayName(status: string): string {
    switch (status) {
      case 'ABERTA':
        return 'Aberta';
      case 'FECHADA':
        return 'Fechada';
      case 'RETIRADA':
        return 'Retirada';
      default:
        return status;
    }
  }

  /**
   * Get maintenance type display name
   */
  getMaintenanceDisplayName(type: string): string {
    switch (type) {
      case 'PREVENTIVA':
        return 'Preventiva';
      case 'CORRETIVA':
        return 'Corretiva';
      default:
        return type;
    }
  }

  /**
   * Format date for display
   */
  formatDate(dateString: string): string {
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('pt-BR');
    } catch (error) {
      return dateString;
    }
  }

  /**
   * Get vehicle display name
   */
  getVeiculoDisplayName(veiculo: any): string {
    if (!veiculo) return 'Veículo não encontrado';
    return `${veiculo.marca} ${veiculo.modelo} (${veiculo.placa})`;
  }

  /**
   * Get mechanics display names
   */
  getMechanicsDisplayNames(os: OrdemServico): string {
    if (os.encerramentos && os.encerramentos.length > 0) {
      const mecanicos = os.encerramentos.map(e => e.nome_mecanico || 'Não informado');
      return mecanicos.join(', ');
    } else if (os.encerrar_os?.nome_mecanico) {
      return os.encerrar_os.nome_mecanico;
    }
    return 'Não informado';
  }

  /**
   * View OS details
   */
  viewOSDetails(os: OrdemServico) {
    // For now, show alert with details
    this.showOSDetailsModal(os);
  }

  /**
   * Add services and parts to OS
   */
  addServicosPecas(os: OrdemServico, event: Event) {
    event.stopPropagation(); // Previne que o clique propague para o card
    this.showAddServicesModal(os);
  }

  /**
   * Show OS details modal
   */
  async showOSDetailsModal(os: OrdemServico) {
    let message = `
Veículo: ${this.getVeiculoDisplayName(os.veiculo)}

Data: ${this.formatDate(os.created_at)}

Status: ${this.getStatusDisplayName(os.situacao_os)}

Problema: ${os.problema_apresentado}

Sistema: ${os.sistema_afetado}

Tipo: ${this.getMaintenanceDisplayName(os.manutencao)}

Hodômetro: ${os.hodometro} km`;

    // Adiciona informações do mecânico se a OS estiver fechada
    if (os.situacao_os === 'FECHADA') {
      // Busca informações do encerramento da OS
      const encerramentos = await this.getEncerramentoInfo(os.id);
      if (encerramentos && encerramentos.length > 0) {
        // Mostra todos os mecânicos que trabalharam na OS
        const mecanicos = encerramentos.map(e => e.nome_mecanico || 'Não informado').join(', ');
        message += `

Mecânicos: ${mecanicos}`;
        
        // Mostra a data da manutenção do primeiro encerramento
        if (encerramentos[0].data_da_manutencao) {
          message += `

Data da Manutenção: ${this.formatDate(encerramentos[0].data_da_manutencao)}`;
        }
        
        // Mostra o tempo total do primeiro encerramento
        if (encerramentos[0].tempo_total) {
          message += `

Tempo Total: ${encerramentos[0].tempo_total}`;
        }
      }
    }

    message = message.trim();

    const alert = await this.alertController.create({
      header: `OS #${os.id}`,
      message: message,
      buttons: ['Fechar'],
      cssClass: 'os-details-alert'
    });
    await alert.present();
  }

  /**
   * Show add services modal
   */
  async showAddServicesModal(os: OrdemServico) {
    const alert = await this.alertController.create({
      header: `Adicionar à OS #${os.id}`,
      message: 'Escolha o que deseja adicionar:',
      buttons: [
        {
          text: 'Cancelar',
          role: 'cancel'
        },
        {
          text: 'Apenas Serviço',
          handler: () => {
            this.addServico(os);
          }
        },
        {
          text: 'Apenas Peça',
          handler: () => {
            this.addPeca(os);
          }
        },
        {
          text: 'Serviço + Peça',
          handler: () => {
            this.addServicoEPeca(os);
          }
        }
      ]
    });
    await alert.present();
  }

  /**
   * Add service only
   */
  addServico(os: OrdemServico) {
    // TODO: Implementar adição de serviço
    this.showErrorToast('Funcionalidade de adicionar serviço será implementada em breve');
  }

  /**
   * Add part only
   */
  addPeca(os: OrdemServico) {
    // TODO: Implementar adição de peça
    this.showErrorToast('Funcionalidade de adicionar peça será implementada em breve');
  }

  /**
   * Add service and part
   */
  addServicoEPeca(os: OrdemServico) {
    // TODO: Implementar adição de serviço e peça
    this.showErrorToast('Funcionalidade de adicionar serviço e peça será implementada em breve');
  }

  /**
   * Get encerramento info for OS
   */
  async getEncerramentoInfo(osId: number): Promise<any[]> {
    try {
      const token = localStorage.getItem('sgos_token');
      const options: any = {
        headers: {
          'Content-Type': 'application/json'
        }
      };
      
      if (token) {
        options.headers['Authorization'] = `Bearer ${token}`;
      }
      
      const response: any = await this.http.get(
        `http://localhost:8000/api/v1/encerrar-os/?abrir_os_id=${osId}`,
        options
      ).toPromise();
      
      if (response && (response.status === 'success' || response.success) && response.data) {
        const encerramentos = response.data.items || [];
        // Filtra apenas os encerramentos que pertencem a esta OS específica
        const encerramentosDaOS = encerramentos.filter((encerramento: any) => 
          encerramento.abrir_os_id === osId
        );
        return encerramentosDaOS;
      }
      
      return [];
    } catch (error: any) {
      console.error('Error loading encerramento info:', error);
      return [];
    }
  }

  /**
   * Load mechanic info for closed OS
   */
  async loadMechanicInfo(os: OrdemServico) {
    if (os.situacao_os === 'FECHADA' && !os.encerrar_os) {
      try {
        const encerramentos = await this.getEncerramentoInfo(os.id);
        console.log(`OS #${os.id} - Encerramentos encontrados:`, encerramentos);
        
        if (encerramentos && encerramentos.length > 0) {
          // Armazena o primeiro encerramento para compatibilidade com o template
          os.encerrar_os = encerramentos[0];
          // Armazena todos os encerramentos para uso futuro
          os.encerramentos = encerramentos;
          console.log(`OS #${os.id} - Mecânicos carregados:`, encerramentos.map(e => e.nome_mecanico));
        }
      } catch (error) {
        console.error('Error loading mechanic info:', error);
      }
    }
  }

  /**
   * Load mechanic info for all closed OS
   */
  async loadMechanicInfoForClosedOS() {
    const closedOS = this.ordensServico.filter(os => os.situacao_os === 'FECHADA');
    
    // Carrega as informações de mecânico para cada OS fechada individualmente
    for (const os of closedOS) {
      await this.loadMechanicInfo(os);
    }
  }

  /**
   * Get OS statistics
   */
  getOSByStatus(status: string): OrdemServico[] {
    return this.ordensServico.filter(os => os.situacao_os === status);
  }

  /**
   * Clear filters
   */
  clearFilters() {
    this.searchTerm = '';
    this.selectedStatus = '';
    this.applyFilters();
  }

  /**
   * Track by function for ngFor optimization
   */
  trackByOSId(index: number, os: OrdemServico): number {
    return os.id;
  }

  /**
   * Track by function for ngFor optimization (vehicles)
   */
  trackByVeiculoId(index: number, veiculo: Veiculo): number {
    return veiculo.id;
  }

  /**
   * Open menu
   */
  async openMenu() {
    await this.menuController.open();
  }

  /**
   * Show success toast
   */
  private async showSuccessToast(message: string) {
    const toast = await this.toastController.create({
      message,
      duration: 3000,
      position: 'top',
      color: 'success'
    });
    await toast.present();
  }

  /**
   * Show error toast
   */
  private async showErrorToast(message: string) {
    const toast = await this.toastController.create({
      message,
      duration: 4000,
      position: 'top',
      color: 'danger'
    });
    await toast.present();
  }
}
<!-- Mobile Header (só para mobile) -->
<ion-header [translucent]="true" class="mobile-only">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-title>{{showCreateForm ? 'Nova OS' : 'Ordens de Serviço'}}</ion-title>
    <ion-buttons slot="end">
      <ion-button fill="clear" (click)="showCreateForm ? hideCreateOS() : showCreateOS()">
        <ion-icon [name]="showCreateForm ? 'close' : 'add'" slot="icon-only"></ion-icon>
      </ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <div class="os-container">
    
    <!-- Desktop Page Header -->
    <div class="desktop-page-header desktop-only" *ngIf="!showCreateForm">
      <h1>Ordens de Serviço</h1>
      <div class="header-actions">
        <ion-button fill="outline" (click)="loadOrdensServico()">
          <ion-icon name="refresh-outline" slot="start"></ion-icon>
          Atualizar
        </ion-button>
        <ion-button color="primary" (click)="showCreateOS()">
          <ion-icon name="add" slot="start"></ion-icon>
          Nova OS
        </ion-button>
      </div>
    </div>

    <!-- Desktop Create Header -->
    <div class="desktop-page-header desktop-only" *ngIf="showCreateForm">
      <h1>Nova Ordem de Serviço</h1>
      <div class="header-actions">
        <ion-button fill="outline" (click)="hideCreateOS()">
          <ion-icon name="close-outline" slot="start"></ion-icon>
          Cancelar
        </ion-button>
      </div>
    </div>
    
    <!-- CREATE FORM -->
    <div *ngIf="showCreateForm">
      <div class="mobile-header mobile-only">
        <h2>Nova Ordem de Serviço</h2>
        <p>Preencha os dados</p>
      </div>

      <ion-card>
        <ion-card-content>
          <!-- Data e Veículo -->
          <div class="form-row">
            <ion-item fill="outline">
              <ion-label position="stacked">Data *</ion-label>
              <ion-input [(ngModel)]="createFormData.data" type="date"></ion-input>
            </ion-item>
            <ion-item fill="outline">
              <ion-label position="stacked">Veículo *</ion-label>
              <ion-select [(ngModel)]="createFormData.veiculo_id" placeholder="Selecione">
                <ion-select-option *ngFor="let v of veiculos" [value]="v.id">
                  {{v.marca}} {{v.modelo}} - {{v.placa}}
                </ion-select-option>
              </ion-select>
            </ion-item>
          </div>

          <!-- Hodômetro e Sistema -->
          <div class="form-row">
            <ion-item fill="outline">
              <ion-label position="stacked">Hodômetro (km) *</ion-label>
              <ion-input [(ngModel)]="createFormData.hodometro" type="text" placeholder="45000"></ion-input>
            </ion-item>
            <ion-item fill="outline">
              <ion-label position="stacked">Sistema *</ion-label>
              <ion-select [(ngModel)]="createFormData.sistema_afetado" placeholder="Selecione">
                <ion-select-option *ngFor="let s of sistemaOptions" [value]="s">{{s}}</ion-select-option>
              </ion-select>
            </ion-item>
          </div>

          <!-- Problema -->
          <ion-item fill="outline">
            <ion-label position="stacked">Problema Apresentado *</ion-label>
            <ion-textarea [(ngModel)]="createFormData.problema_apresentado" rows="3" placeholder="Descreva o problema..."></ion-textarea>
          </ion-item>

          <!-- Causa -->
          <ion-item fill="outline">
            <ion-label position="stacked">Causa da Avaria *</ion-label>
            <ion-textarea [(ngModel)]="createFormData.causa_da_avaria" rows="3" placeholder="Possível causa..."></ion-textarea>
          </ion-item>

          <!-- Tipo -->
          <ion-item fill="outline">
            <ion-label position="stacked">Tipo de Manutenção *</ion-label>
            <ion-select [(ngModel)]="createFormData.manutencao">
              <ion-select-option value="PREVENTIVA">Preventiva</ion-select-option>
              <ion-select-option value="CORRETIVA">Corretiva</ion-select-option>
            </ion-select>
          </ion-item>

          <!-- Botões -->
          <div class="form-buttons">
            <ion-button expand="block" (click)="createOrdemServico()" [disabled]="!isCreateFormValid()" color="primary">
              <ion-icon name="checkmark-outline" slot="start"></ion-icon>
              Criar OS
            </ion-button>
            <ion-button expand="block" fill="outline" (click)="hideCreateOS()" class="mobile-only">
              <ion-icon name="close-outline" slot="start"></ion-icon>
              Cancelar
            </ion-button>
          </div>
        </ion-card-content>
      </ion-card>
    </div>

    <!-- LIST VIEW -->
    <div *ngIf="!showCreateForm">
      <!-- Mobile Header -->
      <div class="mobile-header mobile-only">
        <h2>Ordens de Serviço</h2>
        <p>Gerencie todas as OS</p>
      </div>

      <!-- Filters -->
      <div class="filters">
        <ion-searchbar [(ngModel)]="searchTerm" (ionInput)="onSearchChange($event)" placeholder="Buscar OS..."></ion-searchbar>
        <ion-select [(ngModel)]="selectedStatus" (ionChange)="onStatusChange($event)" placeholder="Status">
          <ion-select-option *ngFor="let opt of statusOptions" [value]="opt.value">{{opt.label}}</ion-select-option>
        </ion-select>
      </div>

      <!-- Stats -->
      <ion-card class="stats-card" *ngIf="!loading">
        <ion-card-content>
          <div class="stats-grid">
            <div class="stat-item">
              <span class="number">{{ordensServico.length}}</span>
              <span class="label">Total</span>
            </div>
            <div class="stat-item">
              <span class="number warning">{{getOSByStatus('ABERTA').length}}</span>
              <span class="label">Abertas</span>
            </div>
            <div class="stat-item">
              <span class="number success">{{getOSByStatus('FECHADA').length}}</span>
              <span class="label">Fechadas</span>
            </div>
            <div class="stat-item">
              <span class="number primary">{{getOSByStatus('RETIRADA').length}}</span>
              <span class="label">Retiradas</span>
            </div>
          </div>
        </ion-card-content>
      </ion-card>

      <!-- Loading -->
      <div *ngIf="loading" class="loading-cards">
        <ion-card *ngFor="let i of [1,2,3]">
          <ion-card-content>
            <ion-skeleton-text animated style="width: 60%; height: 20px;"></ion-skeleton-text>
            <ion-skeleton-text animated style="width: 40%; height: 16px;"></ion-skeleton-text>
            <ion-skeleton-text animated style="width: 80%; height: 14px;"></ion-skeleton-text>
          </ion-card-content>
        </ion-card>
      </div>

      <!-- OS List -->
      <div class="os-grid" *ngIf="!loading">
        <ion-card *ngFor="let os of filteredOrdens; trackBy: trackByOSId" class="os-card">
          <ion-card-header>
            <div class="os-header">
              <div>
                <ion-card-title>OS #{{os.id}}</ion-card-title>
                <ion-card-subtitle>{{formatDate(os.created_at)}}</ion-card-subtitle>
              </div>
              <div class="os-actions">
                <ion-badge [color]="getStatusColor(os.situacao_os)">
                  {{getStatusDisplayName(os.situacao_os)}}
                </ion-badge>
                <!-- Botão para adicionar serviços/peças (só para OS abertas) -->
                <ion-button 
                  *ngIf="os.situacao_os === 'ABERTA'"
                  fill="clear" 
                  size="small" 
                  color="primary"
                  (click)="addServicosPecas(os, $event)"
                  class="add-services-btn">
                  <ion-icon name="add-circle-outline" slot="icon-only"></ion-icon>
                </ion-button>
              </div>
            </div>
          </ion-card-header>
          <ion-card-content (click)="viewOSDetails(os)">
            <div class="os-info">
              <p><ion-icon name="car-outline"></ion-icon> {{getVeiculoDisplayName(os.veiculo)}}</p>
              <p><ion-icon name="warning-outline"></ion-icon> {{os.problema_apresentado}}</p>
              <p><ion-icon name="cog-outline"></ion-icon> {{os.sistema_afetado}}</p>
              <p><ion-icon name="speedometer-outline"></ion-icon> {{os.hodometro}} km</p>
              <!-- Informação do mecânico para OS fechadas -->
              <p *ngIf="os.situacao_os === 'FECHADA'" class="mechanic-info">
                <ion-icon name="person-outline"></ion-icon> 
                <span class="mechanic-label">Mecânicos:</span> 
                <span *ngIf="os.encerrar_os || (os.encerramentos && os.encerramentos.length > 0); else loadingMechanic">
                  {{getMechanicsDisplayNames(os)}}
                </span>
                <ng-template #loadingMechanic>
                  <ion-spinner name="dots" style="width: 16px; height: 16px;"></ion-spinner>
                  <span style="margin-left: 8px;">Carregando...</span>
                </ng-template>
              </p>
            </div>
          </ion-card-content>
        </ion-card>
      </div>

      <!-- Empty State -->
      <div class="empty-state" *ngIf="!loading && filteredOrdens.length === 0">
        <ion-icon name="construct-outline"></ion-icon>
        <h3>Nenhuma OS encontrada</h3>
        <p>{{searchTerm || selectedStatus ? 'Tente ajustar os filtros' : 'Crie a primeira OS'}}</p>
        <ion-button fill="outline" (click)="showCreateOS()" *ngIf="!searchTerm && !selectedStatus">
          <ion-icon name="add" slot="start"></ion-icon>
          Criar OS
        </ion-button>
      </div>
    </div>

  </div>

  <!-- FAB (Mobile) -->
  <ion-fab vertical="bottom" horizontal="end" slot="fixed" class="mobile-only" *ngIf="!showCreateForm">
    <ion-fab-button color="primary" (click)="showCreateOS()">
      <ion-icon name="add"></ion-icon>
    </ion-fab-button>
  </ion-fab>

</ion-content>
