import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { ToastController } from '@ionic/angular';
import { Router } from '@angular/router';

interface OrdemServico {
  id: number;
  numero_os: string;
  veiculo: {
    id: number;
    placa: string;
    modelo: string;
    marca: string;
  };
  situacao_os: string;
  data_abertura: string;
  descricao_problema: string;
}

interface PecaUtilizada {
  id?: number;
  peca_utilizada: string;
  num_ficha: string;
  qtd: string;
  abrir_os_id: number;
  usuario_id?: number;
  created_at?: string;
}

interface ServicoRealizado {
  id?: number;
  servico_realizado: string;
  tempo_de_servico_realizado: string;
  abrir_os_id: number;
  usuario_id?: number;
  created_at?: string;
}

@Component({
  selector: 'app-pecas-servicos',
  templateUrl: './pecas-servicos.page.html',
  styleUrls: ['./pecas-servicos.page.scss'],
})
export class PecasServicosPage implements OnInit {
  ordensAbertas: OrdemServico[] = [];
  ordemSelecionada: OrdemServico | null = null;
  
  pecasForm: FormGroup;
  servicosForm: FormGroup;
  
  pecasUtilizadas: PecaUtilizada[] = [];
  servicosRealizados: ServicoRealizado[] = [];
  
  loading = false;
  showPecasForm = false;
  showServicosForm = false;
  
  currentUser: any = null;

  constructor(
    private formBuilder: FormBuilder,
    private http: HttpClient,
    private toastController: ToastController,
    private router: Router
  ) {
    this.pecasForm = this.formBuilder.group({
      peca_utilizada: ['', Validators.required],
      num_ficha: ['', Validators.required],
      qtd: ['1', Validators.required]
    });

    this.servicosForm = this.formBuilder.group({
      servico_realizado: ['', Validators.required],
      tempo_de_servico_realizado: ['', Validators.required]
    });
  }

  ngOnInit() {
    this.checkAuth();
    this.loadOrdensAbertas();
  }

  checkAuth() {
    const token = localStorage.getItem('sgos_token');
    const userStr = localStorage.getItem('sgos_user');
    
    if (token && userStr) {
      this.currentUser = JSON.parse(userStr);
    } else {
      this.router.navigate(['/login']);
    }
  }

  async loadOrdensAbertas() {
    this.loading = true;
    try {
      const token = localStorage.getItem('sgos_token');
      const options: any = {};
      if (token) {
        options.headers = { 'Authorization': `Bearer ${token}` };
      }

      const response: any = await this.http.get(
        'http://localhost:8000/api/v1/ordens-servico/',
        options
      ).toPromise();

      if (response.status === 'success' || response.success) {
        this.ordensAbertas = response.data.filter((os: OrdemServico) => 
          os.situacao_os === 'ABERTA'
        );
      }
    } catch (error) {
      console.error('Erro ao carregar ordens de serviço:', error);
      await this.showErrorToast('Erro ao carregar ordens de serviço');
    } finally {
      this.loading = false;
    }
  }

  selecionarOrdem(ordem: OrdemServico) {
    this.ordemSelecionada = ordem;
    this.loadPecasUtilizadas();
    this.loadServicosRealizados();
  }

  async loadPecasUtilizadas() {
    if (!this.ordemSelecionada) return;
    
    try {
      const token = localStorage.getItem('sgos_token');
      const options: any = {};
      if (token) {
        options.headers = { 'Authorization': `Bearer ${token}` };
      }

      const response: any = await this.http.get(
        `http://localhost:8000/api/v1/pecas-utilizadas/`,
        options
      ).toPromise();

      if (response.status === 'success' || response.success) {
        this.pecasUtilizadas = (response.data || []).filter((peca: PecaUtilizada) => 
          peca.abrir_os_id === this.ordemSelecionada!.id
        );
      }
    } catch (error) {
      console.error('Erro ao carregar peças utilizadas:', error);
    }
  }

  async loadServicosRealizados() {
    if (!this.ordemSelecionada) return;
    
    try {
      const token = localStorage.getItem('sgos_token');
      const options: any = {};
      if (token) {
        options.headers = { 'Authorization': `Bearer ${token}` };
      }

      const response: any = await this.http.get(
        `http://localhost:8000/api/v1/servicos-realizados/`,
        options
      ).toPromise();

      if (response.status === 'success' || response.success) {
        this.servicosRealizados = (response.data || []).filter((servico: ServicoRealizado) => 
          servico.abrir_os_id === this.ordemSelecionada!.id
        );
      }
    } catch (error) {
      console.error('Erro ao carregar serviços realizados:', error);
    }
  }

  togglePecasForm() {
    this.showPecasForm = !this.showPecasForm;
    this.showServicosForm = false;
  }

  toggleServicosForm() {
    this.showServicosForm = !this.showServicosForm;
    this.showPecasForm = false;
  }

  async adicionarPeca() {
    if (this.pecasForm.invalid || !this.ordemSelecionada) {
      await this.showErrorToast('Preencha todos os campos obrigatórios');
      return;
    }

    const peca: PecaUtilizada = {
      abrir_os_id: this.ordemSelecionada.id,
      peca_utilizada: this.pecasForm.get('peca_utilizada')?.value,
      num_ficha: this.pecasForm.get('num_ficha')?.value,
      qtd: this.pecasForm.get('qtd')?.value,
      usuario_id: this.currentUser?.id
    };

    try {
      const token = localStorage.getItem('sgos_token');
      const options: any = {};
      if (token) {
        options.headers = { 'Authorization': `Bearer ${token}` };
      }

      const response: any = await this.http.post(
        'http://localhost:8000/api/v1/pecas-utilizadas/',
        peca,
        options
      ).toPromise();

      if (response.status === 'success' || response.success) {
        await this.showSuccessToast('Peça adicionada com sucesso!');
        this.pecasForm.reset();
        this.showPecasForm = false;
        await this.loadPecasUtilizadas();
      }
    } catch (error) {
      console.error('Erro ao adicionar peça:', error);
      await this.showErrorToast('Erro ao adicionar peça');
    }
  }

  async adicionarServico() {
    if (this.servicosForm.invalid || !this.ordemSelecionada) {
      await this.showErrorToast('Preencha todos os campos obrigatórios');
      return;
    }

    const servico: ServicoRealizado = {
      abrir_os_id: this.ordemSelecionada.id,
      servico_realizado: this.servicosForm.get('servico_realizado')?.value,
      tempo_de_servico_realizado: this.servicosForm.get('tempo_de_servico_realizado')?.value,
      usuario_id: this.currentUser?.id
    };

    try {
      const token = localStorage.getItem('sgos_token');
      const options: any = {};
      if (token) {
        options.headers = { 'Authorization': `Bearer ${token}` };
      }

      const response: any = await this.http.post(
        'http://localhost:8000/api/v1/servicos-realizados/',
        servico,
        options
      ).toPromise();

      if (response.status === 'success' || response.success) {
        await this.showSuccessToast('Serviço adicionado com sucesso!');
        this.servicosForm.reset();
        this.showServicosForm = false;
        await this.loadServicosRealizados();
      }
    } catch (error) {
      console.error('Erro ao adicionar serviço:', error);
      await this.showErrorToast('Erro ao adicionar serviço');
    }
  }

  async removerPeca(pecaId: number) {
    try {
      const token = localStorage.getItem('sgos_token');
      const options: any = {};
      if (token) {
        options.headers = { 'Authorization': `Bearer ${token}` };
      }

      const response: any = await this.http.delete(
        `http://localhost:8000/api/v1/pecas-utilizadas/${pecaId}`,
        options
      ).toPromise();

      if (response.status === 'success' || response.success) {
        await this.showSuccessToast('Peça removida com sucesso!');
        await this.loadPecasUtilizadas();
      }
    } catch (error) {
      console.error('Erro ao remover peça:', error);
      await this.showErrorToast('Erro ao remover peça');
    }
  }

  async removerServico(servicoId: number) {
    try {
      const token = localStorage.getItem('sgos_token');
      const options: any = {};
      if (token) {
        options.headers = { 'Authorization': `Bearer ${token}` };
      }

      const response: any = await this.http.delete(
        `http://localhost:8000/api/v1/servicos-realizados/${servicoId}`,
        options
      ).toPromise();

      if (response.status === 'success' || response.success) {
        await this.showSuccessToast('Serviço removido com sucesso!');
        await this.loadServicosRealizados();
      }
    } catch (error) {
      console.error('Erro ao remover serviço:', error);
      await this.showErrorToast('Erro ao remover serviço');
    }
  }

  calcularTotalPecas(): number {
    return this.pecasUtilizadas.length;
  }

  calcularTotalServicos(): number {
    return this.servicosRealizados.length;
  }

  calcularTotalGeral(): number {
    return this.calcularTotalPecas() + this.calcularTotalServicos();
  }



  async showSuccessToast(message: string) {
    const toast = await this.toastController.create({
      message: message,
      duration: 2000,
      color: 'success',
      position: 'top'
    });
    await toast.present();
  }

  async showErrorToast(message: string) {
    const toast = await this.toastController.create({
      message: message,
      duration: 3000,
      color: 'danger',
      position: 'top'
    });
    await toast.present();
  }

  hasPermission(role: string): boolean {
    if (!this.currentUser) return false;
    
    const roles: { [key: string]: number } = { 'ADMIN': 4, 'SUPERVISOR': 3, 'MECANICO': 2, 'USUARIO': 1 };
    const userLevel = roles[this.currentUser.perfil as string] || 0;
    const requiredLevel = roles[role] || 0;
    
    return userLevel >= requiredLevel;
  }
}
<ion-header>
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-back-button defaultHref="/dashboard"></ion-back-button>
    </ion-buttons>
    <ion-title>Peças e Serviços</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <div class="container">
    <!-- Loading -->
    <div *ngIf="loading" class="loading-container">
      <ion-spinner name="crescent"></ion-spinner>
      <p>Carregando...</p>
    </div>

    <!-- Conteúdo Principal -->
    <div *ngIf="!loading" class="content">
      <!-- Seleção de Ordem de Serviço -->
      <ion-card>
        <ion-card-header>
          <ion-card-title>
            <ion-icon name="construct-outline"></ion-icon>
            Selecionar Ordem de Serviço
          </ion-card-title>
        </ion-card-header>
        <ion-card-content>
          <div *ngIf="ordensAbertas.length === 0" class="no-data">
            <ion-icon name="information-circle-outline"></ion-icon>
            <p>Nenhuma ordem de serviço aberta encontrada.</p>
          </div>

          <div *ngIf="ordensAbertas.length > 0" class="ordens-grid">
            <ion-card 
              *ngFor="let ordem of ordensAbertas" 
              class="ordem-card"
              [class.selected]="ordemSelecionada?.id === ordem.id"
              (click)="selecionarOrdem(ordem)">
              <ion-card-content>
                <div class="ordem-info">
                  <h3>OS #{{ ordem.numero_os }}</h3>
                  <p><strong>Veículo:</strong> {{ ordem.veiculo.placa }} - {{ ordem.veiculo.marca }} {{ ordem.veiculo.modelo }}</p>
                  <p><strong>Data:</strong> {{ ordem.data_abertura | date:'dd/MM/yyyy' }}</p>
                  <p><strong>Problema:</strong> {{ ordem.descricao_problema }}</p>
                </div>
              </ion-card-content>
            </ion-card>
          </div>
        </ion-card-content>
      </ion-card>

      <!-- Detalhes da Ordem Selecionada -->
      <div *ngIf="ordemSelecionada" class="ordem-details">
        <ion-card>
          <ion-card-header>
            <ion-card-title>
              <ion-icon name="car-outline"></ion-icon>
              Ordem #{{ ordemSelecionada.numero_os }}
            </ion-card-title>
            <ion-card-subtitle>
              {{ ordemSelecionada.veiculo.placa }} - {{ ordemSelecionada.veiculo.marca }} {{ ordemSelecionada.veiculo.modelo }}
            </ion-card-subtitle>
          </ion-card-header>
        </ion-card>

        <!-- Botões de Ação -->
        <div class="action-buttons" *ngIf="hasPermission('MECANICO')">
          <ion-button 
            expand="block" 
            color="primary" 
            (click)="togglePecasForm()"
            [class.active]="showPecasForm">
            <ion-icon name="hardware-chip-outline" slot="start"></ion-icon>
            {{ showPecasForm ? 'Cancelar Peça' : 'Adicionar Peça' }}
          </ion-button>

          <ion-button 
            expand="block" 
            color="secondary" 
            (click)="toggleServicosForm()"
            [class.active]="showServicosForm">
            <ion-icon name="build-outline" slot="start"></ion-icon>
            {{ showServicosForm ? 'Cancelar Serviço' : 'Adicionar Serviço' }}
          </ion-button>
        </div>

        <!-- Formulário de Peças -->
        <ion-card *ngIf="showPecasForm">
          <ion-card-header>
            <ion-card-title>Adicionar Peça</ion-card-title>
          </ion-card-header>
          <ion-card-content>
            <form [formGroup]="pecasForm" (ngSubmit)="adicionarPeca()">
              <ion-item>
                <ion-label position="stacked">Peça Utilizada *</ion-label>
                <ion-input formControlName="peca_utilizada" placeholder="Ex: Filtro de óleo"></ion-input>
              </ion-item>

              <ion-item>
                <ion-label position="stacked">Número da Ficha *</ion-label>
                <ion-input formControlName="num_ficha" placeholder="Ex: FICHA-001"></ion-input>
              </ion-item>

              <ion-item>
                <ion-label position="stacked">Quantidade *</ion-label>
                <ion-input 
                  type="text" 
                  formControlName="qtd" 
                  placeholder="Ex: 1 unidade">
                </ion-input>
              </ion-item>

              <ion-button 
                expand="block" 
                type="submit" 
                [disabled]="pecasForm.invalid">
                <ion-icon name="add-outline" slot="start"></ion-icon>
                Adicionar Peça
              </ion-button>
            </form>
          </ion-card-content>
        </ion-card>

        <!-- Formulário de Serviços -->
        <ion-card *ngIf="showServicosForm">
          <ion-card-header>
            <ion-card-title>Adicionar Serviço</ion-card-title>
          </ion-card-header>
          <ion-card-content>
            <form [formGroup]="servicosForm" (ngSubmit)="adicionarServico()">
              <ion-item>
                <ion-label position="stacked">Serviço Realizado *</ion-label>
                <ion-textarea 
                  formControlName="servico_realizado" 
                  placeholder="Ex: Troca de óleo e filtro"
                  rows="3">
                </ion-textarea>
              </ion-item>

              <ion-item>
                <ion-label position="stacked">Tempo do Serviço *</ion-label>
                <ion-input 
                  type="text" 
                  formControlName="tempo_de_servico_realizado" 
                  placeholder="Ex: 2 horas">
                </ion-input>
              </ion-item>

              <ion-button 
                expand="block" 
                type="submit" 
                [disabled]="servicosForm.invalid">
                <ion-icon name="add-outline" slot="start"></ion-icon>
                Adicionar Serviço
              </ion-button>
            </form>
          </ion-card-content>
        </ion-card>

        <!-- Lista de Peças Utilizadas -->
        <ion-card>
          <ion-card-header>
            <ion-card-title>
              <ion-icon name="hardware-chip-outline"></ion-icon>
              Peças Utilizadas
            </ion-card-title>
          </ion-card-header>
          <ion-card-content>
            <div *ngIf="pecasUtilizadas.length === 0" class="no-data">
              <ion-icon name="information-circle-outline"></ion-icon>
              <p>Nenhuma peça registrada.</p>
            </div>

                         <ion-list *ngIf="pecasUtilizadas.length > 0">
               <ion-item *ngFor="let peca of pecasUtilizadas">
                 <ion-label>
                   <h3>{{ peca.peca_utilizada }}</h3>
                   <p>Ficha: {{ peca.num_ficha }}</p>
                   <p>Quantidade: {{ peca.qtd }}</p>
                 </ion-label>
                 <ion-button 
                   *ngIf="hasPermission('MECANICO')"
                   slot="end" 
                   fill="clear" 
                   color="danger"
                   (click)="removerPeca(peca.id!)">
                   <ion-icon name="trash-outline"></ion-icon>
                 </ion-button>
               </ion-item>
             </ion-list>

                         <div *ngIf="pecasUtilizadas.length > 0" class="total-section">
               <ion-item>
                 <ion-label><strong>Total Peças:</strong></ion-label>
                 <ion-note slot="end">
                   <strong>{{ calcularTotalPecas() }}</strong>
                 </ion-note>
               </ion-item>
             </div>
          </ion-card-content>
        </ion-card>

        <!-- Lista de Serviços Realizados -->
        <ion-card>
          <ion-card-header>
            <ion-card-title>
              <ion-icon name="build-outline"></ion-icon>
              Serviços Realizados
            </ion-card-title>
          </ion-card-header>
          <ion-card-content>
            <div *ngIf="servicosRealizados.length === 0" class="no-data">
              <ion-icon name="information-circle-outline"></ion-icon>
              <p>Nenhum serviço registrado.</p>
            </div>

                         <ion-list *ngIf="servicosRealizados.length > 0">
               <ion-item *ngFor="let servico of servicosRealizados">
                 <ion-label>
                   <h3>{{ servico.servico_realizado }}</h3>
                   <p>Tempo: {{ servico.tempo_de_servico_realizado }}</p>
                 </ion-label>
                 <ion-button 
                   *ngIf="hasPermission('MECANICO')"
                   slot="end" 
                   fill="clear" 
                   color="danger"
                   (click)="removerServico(servico.id!)">
                   <ion-icon name="trash-outline"></ion-icon>
                 </ion-button>
               </ion-item>
             </ion-list>

                         <div *ngIf="servicosRealizados.length > 0" class="total-section">
               <ion-item>
                 <ion-label><strong>Total Serviços:</strong></ion-label>
                 <ion-note slot="end">
                   <strong>{{ calcularTotalServicos() }}</strong>
                 </ion-note>
               </ion-item>
             </div>
          </ion-card-content>
        </ion-card>

        <!-- Resumo Total -->
        <ion-card *ngIf="pecasUtilizadas.length > 0 || servicosRealizados.length > 0" class="total-card">
          <ion-card-header>
            <ion-card-title>
              <ion-icon name="calculator-outline"></ion-icon>
              Resumo Total
            </ion-card-title>
          </ion-card-header>
                     <ion-card-content>
             <ion-item>
               <ion-label>Total Peças:</ion-label>
               <ion-note slot="end">{{ calcularTotalPecas() }}</ion-note>
             </ion-item>
             <ion-item>
               <ion-label>Total Serviços:</ion-label>
               <ion-note slot="end">{{ calcularTotalServicos() }}</ion-note>
             </ion-item>
             <ion-item class="total-geral">
               <ion-label><strong>TOTAL GERAL:</strong></ion-label>
               <ion-note slot="end">
                 <strong>{{ calcularTotalGeral() }}</strong>
               </ion-note>
             </ion-item>
           </ion-card-content>
        </ion-card>
      </div>
    </div>
  </div>
</ion-content>
