import { Component, OnInit, HostListener } from '@angular/core';
import { Router } from '@angular/router';
import { MenuController, Platform } from '@ionic/angular';

@Component({
  selector: 'app-layout',
  templateUrl: './app-layout.component.html',
  styleUrls: ['./app-layout.component.scss'],
})
export class AppLayoutComponent implements OnInit {
  currentUser: any = null;
  isAuthenticated = false;
  isMobile = true;
  screenWidth = 0;

  menuItems = [
    { title: 'Dashboard', url: '/dashboard', icon: 'home-outline' },
    { title: 'Ordens de Serviço', url: '/ordens-servico', icon: 'construct-outline' },
    { title: 'Veículos', url: '/veiculos', icon: 'car-outline' },
    { title: 'Usuários', url: '/usuarios', icon: 'people-outline', role: 'SUPERVISOR' },
    { title: 'Relatórios', url: '/relatorios', icon: 'bar-chart-outline', role: 'SUPERVISOR' },
    { title: 'Configurações', url: '/configuracoes', icon: 'settings-outline', role: 'ADMIN' }
  ];

  constructor(
    private router: Router,
    private menuController: MenuController,
    private platform: Platform
  ) {}

  ngOnInit() {
    this.checkAuth();
    this.updateScreenSize();
  }

  @HostListener('window:resize', ['$event'])
  onResize(event: any) {
    this.updateScreenSize();
  }

  updateScreenSize() {
    this.screenWidth = window.innerWidth;
    this.isMobile = this.screenWidth < 768;
  }

  checkAuth() {
    const token = localStorage.getItem('sgos_token');
    const userStr = localStorage.getItem('sgos_user');
    
    if (token && userStr) {
      this.currentUser = JSON.parse(userStr);
      this.isAuthenticated = true;
    }
  }

  hasPermission(item: any): boolean {
    if (!item.role) return true;
    if (!this.currentUser) return false;
    
    const roles: { [key: string]: number } = { 'ADMIN': 4, 'SUPERVISOR': 3, 'MECANICO': 2, 'USUARIO': 1 };
    const userLevel = roles[this.currentUser.perfil as string] || 0;
    const requiredLevel = roles[item.role] || 0;
    
    return userLevel >= requiredLevel;
  }

  getFilteredMenuItems() {
    return this.menuItems.filter(item => this.hasPermission(item));
  }

  async navigateTo(url: string) {
    await this.router.navigate([url]);
    if (this.isMobile) {
      await this.menuController.close();
    }
  }

  async logout() {
    localStorage.clear();
    this.currentUser = null;
    this.isAuthenticated = false;
    await this.menuController.close();
    await this.router.navigate(['/login']);
  }

  getUserInitials(): string {
    if (!this.currentUser?.nome_completo) return 'U';
    const names = this.currentUser.nome_completo.split(' ');
    return names.length >= 2 ? 
      (names[0][0] + names[1][0]).toUpperCase() : 
      names[0][0].toUpperCase();
  }

  getUserFirstName(): string {
    if (!this.currentUser?.nome_completo) return 'Usuário';
    return this.currentUser.nome_completo.split(' ')[0];
  }

  getRoleDisplayName(): string {
    if (!this.currentUser) return '';
    const roleNames: { [key: string]: string } = {
      'ADMIN': 'Admin', 'SUPERVISOR': 'Supervisor', 
      'MECANICO': 'Mecânico', 'USUARIO': 'Usuário'
    };
    return roleNames[this.currentUser.perfil as string] || this.currentUser.perfil;
  }

  isCurrentRoute(url: string): boolean {
    return this.router.url === url;
  }
}
<ion-app>
  <!-- DESKTOP HEADER (>= 768px) -->
  <div class="desktop-header" *ngIf="!isMobile && isAuthenticated">
    <div class="header-content">
      <!-- Brand -->
      <div class="brand" (click)="navigateTo('/dashboard')">
        <ion-icon name="construct"></ion-icon>
        <span>SGOS</span>
      </div>

      <!-- Navigation -->
      <nav class="desktop-nav">
        <button 
          *ngFor="let item of getFilteredMenuItems()"
          class="nav-item"
          [class.active]="isCurrentRoute(item.url)"
          (click)="navigateTo(item.url)">
          <ion-icon [name]="item.icon"></ion-icon>
          <span>{{item.title}}</span>
        </button>
      </nav>

      <!-- User Menu -->
      <div class="user-menu">
        <div class="user-info">
          <div class="avatar">{{getUserInitials()}}</div>
          <div class="details">
            <span class="name">{{getUserFirstName()}}</span>
            <small class="role">{{getRoleDisplayName()}}</small>
          </div>
        </div>
        <button class="logout-btn" (click)="logout()">
          <ion-icon name="log-out-outline"></ion-icon>
        </button>
      </div>
    </div>
  </div>

  <!-- MOBILE MENU -->
  <ion-menu 
    contentId="main-content" 
    type="overlay" 
    [disabled]="!isAuthenticated || !isMobile">
    
    <ion-header>
      <ion-toolbar color="primary">
        <ion-title>SGOS</ion-title>
      </ion-toolbar>
    </ion-header>
    
    <ion-content>
      <!-- User Info -->
      <div class="mobile-user-info" *ngIf="currentUser">
        <div class="avatar">{{getUserInitials()}}</div>
        <div class="details">
          <h3>{{currentUser.nome_completo}}</h3>
          <p>{{getRoleDisplayName()}}</p>
          <small>{{currentUser.email}}</small>
        </div>
      </div>

      <ion-list>
        <ion-item 
          *ngFor="let item of getFilteredMenuItems()" 
          button 
          (click)="navigateTo(item.url)"
          [class.active]="isCurrentRoute(item.url)">
          <ion-icon [name]="item.icon" slot="start"></ion-icon>
          <ion-label>{{item.title}}</ion-label>
        </ion-item>

        <ion-item-divider>
          <ion-label>Conta</ion-label>
        </ion-item-divider>

        <ion-item button (click)="navigateTo('/perfil')">
          <ion-icon name="person-outline" slot="start"></ion-icon>
          <ion-label>Perfil</ion-label>
        </ion-item>

        <ion-item button (click)="logout()">
          <ion-icon name="log-out-outline" slot="start"></ion-icon>
          <ion-label>Sair</ion-label>
        </ion-item>
      </ion-list>
    </ion-content>
  </ion-menu>

  <!-- MAIN CONTENT -->
  <div class="main-content" [class.with-desktop-header]="!isMobile && isAuthenticated">
    
    <!-- Mobile Header -->
    <ion-header *ngIf="isMobile && isAuthenticated">
      <ion-toolbar color="primary">
        <ion-buttons slot="start">
          <ion-menu-button></ion-menu-button>
        </ion-buttons>
        <ion-title>
          <ion-icon name="construct"></ion-icon>
          SGOS
        </ion-title>
      </ion-toolbar>
    </ion-header>

    <!-- Router Outlet -->
    <ion-router-outlet id="main-content"></ion-router-outlet>
  </div>
</ion-app>
