import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { LoadingController, ToastController, Platform } from '@ionic/angular';

import { AuthService } from '../../../services/auth.service';
import { LoadingState } from '../../../models';

@Component({
  selector: 'app-login',
  templateUrl: './login.page.html',
  styleUrls: ['./login.page.scss'],
})
export class LoginPage implements OnInit {
  loginForm: FormGroup;
  loadingState: LoadingState = {
    loading: false,
    error: null,
    success: false
  };
  
  showPassword = false;
  isMobile = false;

  constructor(
    private formBuilder: FormBuilder,
    private authService: AuthService,
    private router: Router,
    private loadingController: LoadingController,
    private toastController: ToastController,
    private platform: Platform
  ) {
    this.loginForm = this.createForm();
  }

  ngOnInit() {
    this.isMobile = this.platform.is('mobile');
  }

  private createForm(): FormGroup {
    return this.formBuilder.group({
      username: ['', [Validators.required, Validators.minLength(3)]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });
  }

  /**
   * Handle login form submission
   */
  async onLogin() {
    if (this.loginForm.valid) {
      this.loadingState.loading = true;
      this.loadingState.error = null;

      const loading = await this.loadingController.create({
        message: 'Fazendo login...',
        duration: 10000
      });
      await loading.present();

      try {
        const credentials = this.loginForm.value;
        const response = await this.authService.login(credentials).toPromise();

        if (response && (response.status === 'success' || response.success)) {
          this.loadingState.success = true;
          await this.showSuccessToast('Login realizado com sucesso!');
          await this.router.navigate(['/dashboard']);
        } else {
          throw new Error(response?.message || 'Erro no login');
        }
      } catch (error: any) {
        this.loadingState.error = error.message || 'Erro ao fazer login';
        await this.showErrorToast(this.loadingState.error || 'Erro desconhecido');
      } finally {
        this.loadingState.loading = false;
        await loading.dismiss();
      }
    } else {
      await this.showErrorToast('Por favor, preencha todos os campos corretamente');
      this.markFormGroupTouched();
    }
  }

  /**
   * Quick login with demo credentials
   */
  async quickLogin(role: 'admin' | 'mecanico' | 'supervisor' | 'usuario') {
    const credentials = {
      admin: { username: 'admin', password: 'admin123' },
      mecanico: { username: 'mecanico1', password: 'mecanico123' },
      supervisor: { username: 'supervisor', password: 'supervisor123' },
      usuario: { username: 'usuario', password: 'usuario123' }
    };

    this.loginForm.patchValue(credentials[role]);
    await this.onLogin();
  }

  /**
   * Toggle password visibility
   */
  togglePasswordVisibility() {
    this.showPassword = !this.showPassword;
  }

  /**
   * Navigate to forgot password
   */
  goToForgotPassword() {
    this.router.navigate(['/forgot-password']);
  }

  /**
   * Mark all form fields as touched to show validation errors
   */
  private markFormGroupTouched() {
    Object.keys(this.loginForm.controls).forEach(key => {
      const control = this.loginForm.get(key);
      control?.markAsTouched();
    });
  }

  /**
   * Show success toast
   */
  private async showSuccessToast(message: string) {
    const toast = await this.toastController.create({
      message,
      duration: 3000,
      position: 'top',
      color: 'success',
      buttons: [
        {
          text: 'OK',
          role: 'cancel'
        }
      ]
    });
    await toast.present();
  }

  /**
   * Show error toast
   */
  private async showErrorToast(message: string) {
    const toast = await this.toastController.create({
      message,
      duration: 4000,
      position: 'top',
      color: 'danger',
      buttons: [
        {
          text: 'Fechar',
          role: 'cancel'
        }
      ]
    });
    await toast.present();
  }

  /**
   * Get form control error message
   */
  getErrorMessage(controlName: string): string {
    const control = this.loginForm.get(controlName);
    
    if (control?.errors && control.touched) {
      if (control.errors['required']) {
        return `${controlName === 'username' ? 'Usuário' : 'Senha'} é obrigatório`;
      }
      if (control.errors['minlength']) {
        const requiredLength = control.errors['minlength'].requiredLength;
        return `${controlName === 'username' ? 'Usuário' : 'Senha'} deve ter pelo menos ${requiredLength} caracteres`;
      }
    }
    
    return '';
  }

  /**
   * Check if form control has error
   */
  hasError(controlName: string): boolean {
    const control = this.loginForm.get(controlName);
    return !!(control?.errors && control.touched);
  }
}
<ion-content [fullscreen]="true" class="login-content">
  <div class="login-container responsive-container">
    
    <!-- Logo and Title Section -->
    <div class="login-header">
      <div class="logo-container">
        <ion-icon name="construct" class="logo-icon"></ion-icon>
      </div>
      <h1>SGOS</h1>
      <p class="subtitle">Sistema de Gerenciamento de Ordem de Serviço</p>
    </div>

    <!-- Login Form -->
    <form [formGroup]="loginForm" (ngSubmit)="onLogin()" class="login-form responsive-form">
      <ion-card class="form-card responsive-card">
        <ion-card-content>
          
          <!-- Username Field -->
          <div class="form-group">
            <ion-item 
              [class.item-has-error]="hasError('username')"
              fill="outline"
              class="custom-item">
              <ion-icon name="person-outline" slot="start"></ion-icon>
              <ion-label position="stacked">Usuário</ion-label>
              <ion-input 
                formControlName="username"
                type="text"
                placeholder="Digite seu usuário"
                autocomplete="username"
                [clearInput]="true">
              </ion-input>
            </ion-item>
            <div class="error-message" *ngIf="hasError('username')">
              {{ getErrorMessage('username') }}
            </div>
          </div>

          <!-- Password Field -->
          <div class="form-group">
            <ion-item 
              [class.item-has-error]="hasError('password')"
              fill="outline"
              class="custom-item">
              <ion-icon name="lock-closed-outline" slot="start"></ion-icon>
              <ion-label position="stacked">Senha</ion-label>
              <ion-input 
                formControlName="password"
                [type]="showPassword ? 'text' : 'password'"
                placeholder="Digite sua senha"
                autocomplete="current-password">
              </ion-input>
              <ion-button 
                fill="clear" 
                slot="end" 
                (click)="togglePasswordVisibility()"
                class="password-toggle">
                <ion-icon 
                  [name]="showPassword ? 'eye-off-outline' : 'eye-outline'"
                  slot="icon-only">
                </ion-icon>
              </ion-button>
            </ion-item>
            <div class="error-message" *ngIf="hasError('password')">
              {{ getErrorMessage('password') }}
            </div>
          </div>

          <!-- Login Button -->
          <div class="form-group">
            <ion-button 
              type="submit"
              expand="block"
              [disabled]="loadingState.loading"
              class="login-button">
              <ion-spinner *ngIf="loadingState.loading" name="crescent" slot="start"></ion-spinner>
              {{ loadingState.loading ? 'Entrando...' : 'Entrar' }}
            </ion-button>
          </div>

          <!-- Forgot Password Link -->
          <div class="form-group text-center">
            <ion-button 
              fill="clear" 
              size="small"
              (click)="goToForgotPassword()"
              class="forgot-password-button">
              Esqueci minha senha
            </ion-button>
          </div>

        </ion-card-content>
      </ion-card>
    </form>

    <!-- Quick Login Demo Section -->
    <div class="demo-section" *ngIf="!loadingState.loading">
      <ion-card class="demo-card responsive-card">
        <ion-card-header>
          <ion-card-title class="demo-title">
            <ion-icon name="flash-outline"></ion-icon>
            Login Rápido (Demo)
          </ion-card-title>
        </ion-card-header>
        <ion-card-content>
          <div class="demo-buttons">
            <ion-button 
              fill="outline" 
              size="small"
              (click)="quickLogin('admin')"
              class="demo-button">
              <ion-icon name="shield-outline" slot="start"></ion-icon>
              Admin
            </ion-button>
            <ion-button 
              fill="outline" 
              size="small"
              (click)="quickLogin('supervisor')"
              class="demo-button">
              <ion-icon name="people-outline" slot="start"></ion-icon>
              Supervisor
            </ion-button>
            <ion-button 
              fill="outline" 
              size="small"
              (click)="quickLogin('mecanico')"
              class="demo-button">
              <ion-icon name="construct-outline" slot="start"></ion-icon>
              Mecânico
            </ion-button>
            <ion-button 
              fill="outline" 
              size="small"
              (click)="quickLogin('usuario')"
              class="demo-button">
              <ion-icon name="person-outline" slot="start"></ion-icon>
              Usuário
            </ion-button>
          </div>
        </ion-card-content>
      </ion-card>
    </div>

    <!-- Footer -->
    <div class="login-footer">
      <p>&copy; 2024 SGOS. Todos os direitos reservados.</p>
    </div>

  </div>
</ion-content>
