(-2px);\n}\n.os-grid[_ngcontent-%COMP%]   ion-card[_ngcontent-%COMP%]   .os-header[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: space-between;\n  align-items: flex-start;\n}\n.os-grid[_ngcontent-%COMP%]   ion-card[_ngcontent-%COMP%]   .os-header[_ngcontent-%COMP%]   .os-actions[_ngcontent-%COMP%] {\n  display: flex;\n  align-items: center;\n  gap: 8px;\n}\n.os-grid[_ngcontent-%COMP%]   ion-card[_ngcontent-%COMP%]   .os-header[_ngcontent-%COMP%]   .os-actions[_ngcontent-%COMP%]   .add-services-btn[_ngcontent-%COMP%] {\n  --padding-start: 4px;\n  --padding-end: 4px;\n  height: 32px;\n  width: 32px;\n  margin: 0;\n}\n.os-grid[_ngcontent-%COMP%]   ion-card[_ngcontent-%COMP%]   .os-info[_ngcontent-%COMP%]   p[_ngcontent-%COMP%] {\n  display: flex;\n  align-items: center;\n  gap: 8px;\n  margin: 8px 0;\n  font-size: 13px;\n}\n.os-grid[_ngcontent-%COMP%]   ion-card[_ngcontent-%COMP%]   .os-info[_ngcontent-%COMP%]   p[_ngcontent-%COMP%]   ion-icon[_ngcontent-%COMP%] {\n  color: var(--ion-color-medium);\n  flex-shrink: 0;\n}\n\n.empty-state[_ngcontent-%COMP%] {\n  text-align: center;\n  padding: 40px 20px;\n}\n.empty-state[_ngcontent-%COMP%]   ion-icon[_ngcontent-%COMP%] {\n  font-size: 64px;\n  color: var(--ion-color-medium);\n  opacity: 0.5;\n  margin-bottom: 16px;\n}\n.empty-state[_ngcontent-%COMP%]   h3[_ngcontent-%COMP%] {\n  color: var(--ion-color-dark);\n  margin-bottom: 8px;\n}\n.empty-state[_ngcontent-%COMP%]   p[_ngcontent-%COMP%] {\n  color: var(--ion-color-medium);\n  margin-bottom: 20px;\n}\n\n.loading-cards[_ngcontent-%COMP%] {\n  display: grid;\n  gap: 16px;\n  grid-template-columns: 1fr;\n}\n@media (min-width: 768px) {\n  .loading-cards[_ngcontent-%COMP%] {\n    grid-template-columns: repeat(2, 1fr);\n  }\n}\n@media (min-width: 1024px) {\n  .loading-cards[_ngcontent-%COMP%] {\n    grid-template-columns: repeat(3, 1fr);\n  }\n}\n.loading-cards[_ngcontent-%COMP%]   ion-card[_ngcontent-%COMP%] {\n  margin: 0;\n}\n\n.os-details-alert[_ngcontent-%COM