import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { MenuController, Platform } from '@ionic/angular';

interface MenuItem {
  title: string;
  url: string;
  icon: string;
  requiredRole?: string;
}

@Component({
  selector: 'app-root',
  templateUrl: 'app-responsive.component.html',
  styleUrls: ['app-responsive.component.scss'],
})
export class AppComponent implements OnInit {
  currentUser: any = null;
  isAuthenticated = false;
  isMobile = false;
  isTablet = false;
  isDesktop = false;

  mainMenuItems: MenuItem[] = [
    { title: 'Dashboard', url: '/dashboard', icon: 'home-outline' },
    { title: 'Ordens de Serviço', url: '/ordens-servico', icon: 'construct-outline' },
    { title: 'Veículos', url: '/veiculos', icon: 'car-outline' },
    { title: 'Usuários', url: '/usuarios', icon: 'people-outline', requiredRole: 'SUPERVISOR' },
    { title: 'Relatórios', url: '/relatorios', icon: 'bar-chart-outline', requiredRole: 'SUPERVISOR' },
    { title: 'Configurações', url: '/configuracoes', icon: 'settings-outline', requiredRole: 'ADMIN' }
  ];

  userMenuItems: MenuItem[] = [
    { title: 'Perfil', url: '/perfil', icon: 'person-outline' }
  ];

  constructor(
    private router: Router,
    private menuController: MenuController,
    private platform: Platform
  ) {}

  ngOnInit() {
    // Check authentication
    this.checkAuthentication();
    
    // Set up responsive breakpoints
    this.updateScreenSize();
    
    // Listen for screen size changes
    this.platform.resize.subscribe(() => {
      this.updateScreenSize();
    });
  }

  /**
   * Check if user is authenticated
   */
  checkAuthentication() {
    const token = localStorage.getItem('sgos_token');
    const userStr = localStorage.getItem('sgos_user');
    
    if (token && userStr) {
      this.currentUser = JSON.parse(userStr);
      this.isAuthenticated = true;
    } else {
      this.isAuthenticated = false;
      this.currentUser = null;
    }
  }

  /**
   * Update screen size flags
   */
  updateScreenSize() {
    const width = this.platform.width();
    
    this.isMobile = width < 768;
    this.isTablet = width >= 768 && width < 1024;
    this.isDesktop = width >= 1024;
  }

  /**
   * Check if user has permission for menu item
   */
  hasPermission(item: MenuItem): boolean {
    if (!item.requiredRole) return true;
    if (!this.currentUser) return false;
    
    const roleHierarchy = {
      'ADMIN': 4,
      'SUPERVISOR': 3,
      'MECANICO': 2,
      'USUARIO': 1
    };

    const userLevel = roleHierarchy[this.currentUser.perfil as keyof typeof roleHierarchy] || 0;
    const requiredLevel = roleHierarchy[item.requiredRole as keyof typeof roleHierarchy] || 0;

    return userLevel >= requiredLevel;
  }

  /**
   * Get filtered menu items
   */
  getFilteredMenuItems(): MenuItem[] {
    return this.mainMenuItems.filter(item => this.hasPermission(item));
  }

  /**
   * Navigate to page
   */
  async navigateTo(url: string) {
    await this.router.navigate([url]);
    
    // Close mobile menu if open
    if (this.isMobile) {
      await this.menuController.close();
    }
  }

  /**
   * Toggle mobile menu
   */
  async toggleMobileMenu() {
    await this.menuController.toggle();
  }

  /**
   * Logout user
   */
  async logout() {
    localStorage.removeItem('sgos_token');
    localStorage.removeItem('sgos_user');
    this.currentUser = null;
    this.isAuthenticated = false;
    await this.menuController.close();
    await this.router.navigate(['/login']);
  }

  /**
   * Get user initials for avatar
   */
  getUserInitials(): string {
    if (!this.currentUser?.nome_completo) return 'U';
    const names = this.currentUser.nome_completo.split(' ');
    if (names.length >= 2) {
      return (names[0][0] + names[1][0]).toUpperCase();
    }
    return names[0][0].toUpperCase();
  }

  /**
   * Get user role display name
   */
  getUserRoleDisplayName(): string {
    if (!this.currentUser) return '';
    const roleNames = {
      'ADMIN': 'Administrador',
      'SUPERVISOR': 'Supervisor',
      'MECANICO': 'Mecânico',
      'USUARIO': 'Usuário'
    };
    return roleNames[this.currentUser.perfil as keyof typeof roleNames] || this.currentUser.perfil;
  }

  /**
   * Get current route for highlighting
   */
  isCurrentRoute(url: string): boolean {
    return this.router.url === url;
  }

  /**
   * Get user first name
   */
  getUserFirstName(): string {
    if (!this.currentUser?.nome_completo) return 'Usuário';
    return this.currentUser.nome_completo.split(' ')[0];
  }
}
<ion-app>
  <!-- DESKTOP/TABLET HEADER (>= 768px) -->
  <ion-header *ngIf="!isMobile && isAuthenticated" class="desktop-header">
    <ion-toolbar color="primary">
      <!-- Logo/Brand -->
      <div slot="start" class="brand-section">
        <ion-icon name="construct" class="brand-icon"></ion-icon>
        <span class="brand-text">SGOS</span>
      </div>

      <!-- Desktop Navigation -->
      <div class="desktop-nav">
        <ion-button 
          *ngFor="let item of getFilteredMenuItems()"
          fill="clear"
          [class.active-nav-item]="isCurrentRoute(item.url)"
          (click)="navigateTo(item.url)"
          class="nav-button">
          <ion-icon [name]="item.icon" slot="start"></ion-icon>
          {{item.title}}
        </ion-button>
      </div>

      <!-- User Menu -->
      <div slot="end" class="user-section">
        <ion-button fill="clear" class="user-button" id="user-menu-trigger">
          <div class="user-avatar">
            {{getUserInitials()}}
          </div>
          <div class="user-info">
            <span class="user-name">{{getUserFirstName()}}</span>
            <small class="user-role">{{getUserRoleDisplayName()}}</small>
          </div>
          <ion-icon name="chevron-down-outline" class="dropdown-icon"></ion-icon>
        </ion-button>

        <!-- User Dropdown Menu -->
        <ion-popover trigger="user-menu-trigger" placement="bottom-end">
          <ng-template>
            <ion-content class="user-popover">
              <ion-list>
                <ion-item button (click)="navigateTo('/perfil')">
                  <ion-icon name="person-outline" slot="start"></ion-icon>
                  <ion-label>Meu Perfil</ion-label>
                </ion-item>
                <ion-item button (click)="navigateTo('/configuracoes')" *ngIf="hasPermission({title: '', url: '', icon: '', requiredRole: 'ADMIN'})">
                  <ion-icon name="settings-outline" slot="start"></ion-icon>
                  <ion-label>Configurações</ion-label>
                </ion-item>
                <ion-item-divider></ion-item-divider>
                <ion-item button (click)="logout()" class="logout-item">
                  <ion-icon name="log-out-outline" slot="start"></ion-icon>
                  <ion-label>Sair</ion-label>
                </ion-item>
              </ion-list>
            </ion-content>
          </ng-template>
        </ion-popover>
      </div>
    </ion-toolbar>
  </ion-header>

  <!-- MOBILE SIDE MENU (< 768px) -->
  <ion-menu 
    contentId="main-content" 
    type="overlay" 
    [disabled]="!isAuthenticated || !isMobile"
    class="mobile-menu">
    
    <ion-header>
      <ion-toolbar color="primary">
        <ion-title>SGOS</ion-title>
      </ion-toolbar>
    </ion-header>
    
    <ion-content>
      <!-- User Info -->
      <div class="mobile-user-info" *ngIf="currentUser">
        <div class="user-avatar-mobile">
          {{getUserInitials()}}
        </div>
        <div class="user-details-mobile">
          <h3>{{currentUser.nome_completo}}</h3>
          <p>{{getUserRoleDisplayName()}}</p>
          <small>{{currentUser.email}}</small>
        </div>
      </div>

      <ion-list>
        <!-- Main Menu Items -->
        <ion-item 
          *ngFor="let item of getFilteredMenuItems()" 
          button 
          (click)="navigateTo(item.url)"
          [class.active-menu-item]="isCurrentRoute(item.url)">
          <ion-icon [name]="item.icon" slot="start"></ion-icon>
          <ion-label>{{item.title}}</ion-label>
        </ion-item>

        <ion-item-divider>
          <ion-label>Conta</ion-label>
        </ion-item-divider>

        <!-- User Menu Items -->
        <ion-item 
          *ngFor="let item of userMenuItems" 
          button 
          (click)="navigateTo(item.url)"
          [class.active-menu-item]="isCurrentRoute(item.url)">
          <ion-icon [name]="item.icon" slot="start"></ion-icon>
          <ion-label>{{item.title}}</ion-label>
        </ion-item>

        <!-- Logout -->
        <ion-item button (click)="logout()">
          <ion-icon name="log-out-outline" slot="start"></ion-icon>
          <ion-label>Sair</ion-label>
        </ion-item>
      </ion-list>
    </ion-content>
  </ion-menu>

  <!-- MAIN CONTENT -->
  <div class="main-layout" [class.desktop-layout]="isDesktop" [class.mobile-layout]="isMobile">
    
    <!-- Mobile Header (< 768px) -->
    <ion-header *ngIf="isMobile && isAuthenticated" class="mobile-header">
      <ion-toolbar color="primary">
        <ion-buttons slot="start">
          <ion-menu-button></ion-menu-button>
        </ion-buttons>
        <ion-title class="mobile-title">
          <ion-icon name="construct" class="mobile-brand-icon"></ion-icon>
          SGOS
        </ion-title>
        <ion-buttons slot="end">
          <ion-button fill="clear" (click)="logout()">
            <ion-icon name="log-out-outline" slot="icon-only"></ion-icon>
          </ion-button>
        </ion-buttons>
      </ion-toolbar>
    </ion-header>

    <!-- Router Outlet -->
    <ion-router-outlet id="main-content" [class.with-desktop-header]="!isMobile && isAuthenticated"></ion-router-outlet>
  </div>
</ion-app>
