import { Component, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { MenuController, Platform } from '@ionic/angular';
import { Subject } from 'rxjs';
import { takeUntil, finalize } from 'rxjs/operators';

import { AuthService } from '../../services/auth.service';
import { OrdemServicoService } from '../../services/ordem-servico.service';
import { VeiculoService } from '../../services/veiculo.service';
import { UsuarioService } from '../../services/usuario.service';
import { Usuario, OrdemServico } from '../../models';

interface DashboardStats {
  ordensServico: {
    total: number;
    abertas: number;
    fechadas: number;
    retiradas: number;
  };
  veiculos: {
    total: number;
    ativos: number;
    manutencao: number;
  };
  usuarios: {
    total: number;
    ativos: number;
  };
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.page.html',
  styleUrls: ['./dashboard.page.scss'],
})
export class DashboardPage implements OnInit, OnDestroy {
  private destroy$ = new Subject<void>();

  currentUser: Usuario | null = null;
  stats: DashboardStats = {
    ordensServico: { total: 0, abertas: 0, fechadas: 0, retiradas: 0 },
    veiculos: { total: 0, ativos: 0, manutencao: 0 },
    usuarios: { total: 0, ativos: 0 }
  };
  
  recentOrdens: OrdemServico[] = [];
  loading = {
    stats: true,
    ordens: true
  };
  
  isMobile = false;
  refreshing = false;

  constructor(
    private authService: AuthService,
    private ordemServicoService: OrdemServicoService,
    private veiculoService: VeiculoService,
    private usuarioService: UsuarioService,
    private router: Router,
    private menuController: MenuController,
    private platform: Platform
  ) {}

  ngOnInit() {
    this.isMobile = this.platform.is('mobile');
    
    // Subscribe to current user
    this.authService.currentUser$
      .pipe(takeUntil(this.destroy$))
      .subscribe(user => {
        this.currentUser = user;
      });

    this.loadDashboardData();
  }

  ngOnDestroy() {
    this.destroy$.next();
    this.destroy$.complete();
  }

  /**
   * Load all dashboard data
   */
  async loadDashboardData() {
    await Promise.all([
      this.loadStats(),
      this.loadRecentOrdens()
    ]);
  }

  /**
   * Load dashboard statistics
   */
  private async loadStats() {
    this.loading.stats = true;

    try {
      // Load OS stats
      const osStats = await this.ordemServicoService.getOSStats().toPromise();
      if (osStats) {
        this.stats.ordensServico = {
          total: osStats.total,
          abertas: osStats.abertas,
          fechadas: osStats.fechadas,
          retiradas: osStats.retiradas
        };
      }

      // Load vehicle stats if user has permission
      if (this.authService.hasPermission('MECANICO')) {
        const veiculoStats = await this.veiculoService.getVeiculoStats().toPromise();
        if (veiculoStats) {
          this.stats.veiculos = {
            total: veiculoStats.total,
            ativos: veiculoStats.ativos,
            manutencao: veiculoStats.manutencao
          };
        }
      }

      // Load user stats if user has permission
      if (this.authService.hasPermission('SUPERVISOR')) {
        const userStats = await this.usuarioService.getUserStats().toPromise();
        if (userStats) {
          this.stats.usuarios = {
            total: userStats.total,
            ativos: userStats.ativos
          };
        }
      }

    } catch (error) {
      console.error('Error loading stats:', error);
    } finally {
      this.loading.stats = false;
    }
  }

  /**
   * Load recent service orders
   */
  private async loadRecentOrdens() {
    this.loading.ordens = true;

    try {
      this.recentOrdens = await this.ordemServicoService.getRecentOrdens(5).toPromise() || [];
    } catch (error) {
      console.error('Error loading recent orders:', error);
      this.recentOrdens = [];
    } finally {
      this.loading.ordens = false;
    }
  }

  /**
   * Handle pull-to-refresh
   */
  async onRefresh(event: any) {
    this.refreshing = true;
    
    try {
      await this.loadDashboardData();
    } finally {
      this.refreshing = false;
      event.target.complete();
    }
  }

  /**
   * Toggle menu
   */
  async openMenu() {
    await this.menuController.open();
  }

  /**
   * Navigate to specific page
   */
  navigateTo(path: string) {
    this.router.navigate([path]);
  }

  /**
   * Navigate to create new OS
   */
  createNewOS() {
    this.router.navigate(['/ordens-servico/criar']);
  }

  /**
   * Navigate to OS details
   */
  viewOS(osId: number) {
    this.router.navigate(['/ordens-servico', osId]);
  }

  /**
   * Get greeting message based on time
   */
  getGreetingMessage(): string {
    const hour = new Date().getHours();
    
    if (hour < 12) {
      return 'Bom dia';
    } else if (hour < 18) {
      return 'Boa tarde';
    } else {
      return 'Boa noite';
    }
  }

  /**
   * Get user first name
   */
  getUserFirstName(): string {
    if (!this.currentUser?.nome_completo) return 'Usuário';
    return this.currentUser.nome_completo.split(' ')[0];
  }

  /**
   * Get status color for OS
   */
  getStatusColor(status: string): string {
    switch (status) {
      case 'ABERTA':
        return 'warning';
      case 'FECHADA':
        return 'success';
      case 'RETIRADA':
        return 'primary';
      default:
        return 'medium';
    }
  }

  /**
   * Get status display name
   */
  getStatusDisplayName(status: string): string {
    switch (status) {
      case 'ABERTA':
        return 'Aberta';
      case 'FECHADA':
        return 'Fechada';
      case 'RETIRADA':
        return 'Retirada';
      default:
        return status;
    }
  }

  /**
   * Format date for display
   */
  formatDate(dateString: string): string {
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('pt-BR', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric'
      });
    } catch (error) {
      return dateString;
    }
  }

  /**
   * Check if user has permission
   */
  hasPermission(role: string): boolean {
    return this.authService.hasPermission(role);
  }

  /**
   * Track by function for ngFor optimization
   */
  trackByOrderId(index: number, ordem: OrdemServico): number {
    return ordem.id;
  }
}
