import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { ToastController } from '@ionic/angular';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-login-form',
  templateUrl: './login-form.component.html',
  styleUrls: ['./login-form.component.scss'],
})
export class LoginFormComponent implements OnInit {
  loginForm: FormGroup;
  loading = false;
  showPassword = false;

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private toastController: ToastController,
    private http: HttpClient
  ) {
    this.loginForm = this.createForm();
  }

  ngOnInit() {}

  private createForm(): FormGroup {
    return this.formBuilder.group({
      username: ['admin', [Validators.required]],
      password: ['admin123', [Validators.required]]
    });
  }

  async onLogin() {
    if (this.loginForm.valid) {
      this.loading = true;

      try {
        const credentials = this.loginForm.value;
        
        const response: any = await this.http.post('http://localhost:8000/api/v1/auth/login', credentials).toPromise();
        
        console.log('Login response:', response);
        
        if (response && (response.status === 'success' || response.success)) {
          await this.showSuccessToast('Login realizado com sucesso!');
          
          // Store token and user data
          if (response.data?.access_token) {
            localStorage.setItem('sgos_token', response.data.access_token);
            localStorage.setItem('sgos_user', JSON.stringify(response.data.user));
          }
          
          // Navigate to dashboard
          await this.router.navigate(['/dashboard']);
        } else {
          throw new Error(response?.message || 'Erro no login');
        }
      } catch (error: any) {
        console.error('Login error:', error);
        await this.showErrorToast(error.message || 'Erro ao fazer login');
      } finally {
        this.loading = false;
      }
    }
  }

  async quickLogin(role: 'admin' | 'mecanico' | 'supervisor' | 'usuario') {
    const credentials = {
      admin: { username: 'admin', password: 'admin123' },
      mecanico: { username: 'mecanico1', password: 'mecanico123' },
      supervisor: { username: 'supervisor', password: 'supervisor123' },
      usuario: { username: 'usuario', password: 'usuario123' }
    };

    this.loginForm.patchValue(credentials[role]);
    await this.onLogin();
  }

  togglePasswordVisibility() {
    this.showPassword = !this.showPassword;
  }

  goToForgotPassword() {
    this.router.navigate(['/forgot-password']);
  }

  private async showSuccessToast(message: string) {
    const toast = await this.toastController.create({
      message,
      duration: 3000,
      position: 'top',
      color: 'success'
    });
    await toast.present();
  }

  private async showErrorToast(message: string) {
    const toast = await this.toastController.create({
      message,
      duration: 4000,
      position: 'top',
      color: 'danger'
    });
    await toast.present();
  }

  getErrorMessage(controlName: string): string {
    return '';
  }

  hasError(controlName: string): boolean {
    return false;
  }
}
<ion-content [fullscreen]="true" class="login-content">
  <div class="login-container responsive-container">
    
    <!-- Logo and Title Section -->
    <div class="login-header">
      <div class="logo-container">
        <ion-icon name="construct" class="logo-icon"></ion-icon>
      </div>
      <h1>SGOS</h1>
      <p class="subtitle">Sistema de Gerenciamento de Ordem de Serviço</p>
    </div>

    <!-- Login Form -->
    <form [formGroup]="loginForm" (ngSubmit)="onLogin()" class="login-form">
      <ion-card class="form-card">
        <ion-card-content>
          
          <!-- Username Field -->
          <div class="form-group">
            <ion-item fill="outline" class="custom-item">
              <ion-icon name="person-outline" slot="start"></ion-icon>
              <ion-label position="stacked">Usuário</ion-label>
              <ion-input 
                formControlName="username"
                type="text"
                placeholder="Digite seu usuário">
              </ion-input>
            </ion-item>
          </div>

          <!-- Password Field -->
          <div class="form-group">
            <ion-item fill="outline" class="custom-item">
              <ion-icon name="lock-closed-outline" slot="start"></ion-icon>
              <ion-label position="stacked">Senha</ion-label>
              <ion-input 
                formControlName="password"
                [type]="showPassword ? 'text' : 'password'"
                placeholder="Digite sua senha">
              </ion-input>
              <ion-button 
                fill="clear" 
                slot="end" 
                (click)="togglePasswordVisibility()">
                <ion-icon 
                  [name]="showPassword ? 'eye-off-outline' : 'eye-outline'"
                  slot="icon-only">
                </ion-icon>
              </ion-button>
            </ion-item>
          </div>

          <!-- Login Button -->
          <div class="form-group">
            <ion-button 
              type="submit"
              expand="block"
              [disabled]="loading"
              class="login-button">
              <ion-spinner *ngIf="loading" name="crescent" slot="start"></ion-spinner>
              {{ loading ? 'Entrando...' : 'Entrar' }}
            </ion-button>
          </div>

        </ion-card-content>
      </ion-card>
    </form>

    <!-- Quick Login Demo Section -->
    <div class="demo-section">
      <ion-card class="demo-card">
        <ion-card-header>
          <ion-card-title class="demo-title">
            <ion-icon name="flash-outline"></ion-icon>
            Login Rápido (Demo)
          </ion-card-title>
        </ion-card-header>
        <ion-card-content>
          <div class="demo-buttons">
            <ion-button fill="outline" size="small" (click)="quickLogin('admin')" class="demo-button">
              <ion-icon name="shield-outline" slot="start"></ion-icon>
              Admin
            </ion-button>
            <ion-button fill="outline" size="small" (click)="quickLogin('supervisor')" class="demo-button">
              <ion-icon name="people-outline" slot="start"></ion-icon>
              Supervisor
            </ion-button>
            <ion-button fill="outline" size="small" (click)="quickLogin('mecanico')" class="demo-button">
              <ion-icon name="construct-outline" slot="start"></ion-icon>
              Mecânico
            </ion-button>
            <ion-button fill="outline" size="small" (click)="quickLogin('usuario')" class="demo-button">
              <ion-icon name="person-outline" slot="start"></ion-icon>
              Usuário
            </ion-button>
          </div>
        </ion-card-content>
      </ion-card>
    </div>

    <!-- Footer -->
    <div class="login-footer">
      <p>&copy; 2024 SGOS. Todos os direitos reservados.</p>
    </div>

  </div>
</ion-content>
